sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var whatneeded = g_form.getValue('u_what_needed');

   //clear all the choices from what needed field
	g_form.clearOptions('u_what_needed');

	if(newValue == 'hr')
		{
			g_form.addOption('u_what_needed','hr1','HumanResources1');
			g_form.addOption('u_what_needed','hr2','HumanResources21');
			g_form.addOption('u_what_needed','hr3','HumanResources3');

		}
	if(newValue == 'Facilities')
		{
			g_form.addOption('u_what_needed','Facilities1','Facilities1');
			g_form.addOption('u_what_needed','Facilities2','Facilities2');
			g_form.addOption('u_what_needed','Facilities3','Facilities3');

		}
	if(newValue == 'Legal')
		{
			g_form.addOption('u_what_needed','Legal1','Legal1');
			g_form.addOption('u_what_needed','Legal2','Legal2');
			g_form.addOption('u_what_needed','Legal3','Legal3');

		}

   if(isLoading && !g_form.isNewRecord())
	   {
		   g_form.setValue('u_what_needed',whatneeded)
	   }
}
